  `AMZARTTAX` double NOT NULL,
  `AMZSHIPPRICE` double NOT NULL,
  `AMZSHIPTAX` double NOT NULL,
  `AZTIMESTAMP` timestamp NOT NULL,
  PRIMARY KEY  (`AMZORDERID`,`AMZORDERITEMCODE`)
) TYPE=MyISAM;

